import React, { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/Auth"
import {
  FiMenu,
  FiX,
  FiUser,
  FiLogOut,
  FiHome,
  FiBook,
  FiCalendar,
} from "react-icons/fi"

const Navbar = () => {
  const { user, logout } = useAuth()
  const navigate = useNavigate()
  const [isMenuOpen, setIsMenuOpen] = useState(false)

  // Get user profile image from localStorage
  const getUserProfileImage = () => {
    if (!user?.id) return null
    const savedProfile = localStorage.getItem(`profile_${user.id}`)
    if (savedProfile) {
      const parsedProfile = JSON.parse(savedProfile)
      return parsedProfile.profileImage
    }
    return null
  }

  const profileImage = getUserProfileImage()

  const handleLogout = () => {
    logout()
    navigate("/")
    setIsMenuOpen(false)
  }

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen)
  }

  const closeMenu = () => {
    setIsMenuOpen(false)
  }

  return (
    <nav className="navbar" aria-label="Main Navigation">
      <div className="container">
        <div className="navbar-content">
          <Link
            to="/"
            className="navbar-brand"
            onClick={closeMenu}
            tabIndex={0}
            aria-label="Fantastic Broccoli Home"
          >
            <span
              className="brand-icon"
              style={{
                fontSize: "2.2rem",
                marginRight: "0.5rem",
                filter: "drop-shadow(0 2px 8px #10b98144)",
              }}
            >
              🥦
            </span>
            <span
              style={{
                background:
                  "linear-gradient(90deg, #10b981 0%, #059669 100%)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
                fontWeight: 900,
                letterSpacing: "0.04em",
                fontSize: "2rem",
                textShadow: "0 2px 8px #10b98122",
              }}
            >
              Fantastic Broccoli
            </span>
          </Link>

          {/* Grouped main navigation links */}
          <nav className="main-nav-links" aria-label="Primary">
            <ul
              className={`navbar-links ${isMenuOpen ? "active" : ""}`}
              style={{
                listStyle: "none",
                display: "flex",
                gap: "2.5rem",
                margin: 0,
                padding: 0,
              }}
            >
              <li>
                <Link
                  to="/"
                  className="nav-link"
                  onClick={closeMenu}
                  tabIndex={0}
                  aria-label="Home"
                >
                  <FiHome style={{ marginRight: 6 }} /> Home
                </Link>
              </li>
              <li>
                <Link
                  to="/recipes"
                  className="nav-link"
                  onClick={closeMenu}
                  tabIndex={0}
                  aria-label="Recipes"
                >
                  <FiBook style={{ marginRight: 6 }} /> Recipes
                </Link>
              </li>
              {user && (
                <li>
                  <Link
                    to="/meal-planner"
                    className="nav-link"
                    onClick={closeMenu}
                    tabIndex={0}
                    aria-label="Meal Planner"
                  >
                    <FiCalendar style={{ marginRight: 6 }} /> Meal Planner
                  </Link>
                </li>
              )}
            </ul>
          </nav>

          <div
            className={`navbar-auth ${isMenuOpen ? "active" : ""}`}
            style={{
              display: "flex",
              alignItems: "center",
              gap: "1.5rem",
            }}
          >
            {user ? (
              <div
                className="user-menu"
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "1rem",
                }}
              >
                <Link
                  to="/profile"
                  className="nav-link profile-link"
                  onClick={closeMenu}
                  tabIndex={0}
                  aria-label="Profile"
                  style={{
                    display: "flex",
                    alignItems: "center",
                    fontWeight: 700,
                  }}
                >
                  {profileImage ? (
                    <img
                      src={profileImage}
                      alt="Profile"
                      className="navbar-avatar"
                      style={{
                        width: 36,
                        height: 36,
                        borderRadius: "50%",
                        objectFit: "cover",
                        marginRight: 10,
                        border: "2.5px solid #10b981",
                        boxShadow: "0 2px 8px #10b98111",
                        transition: "box-shadow 0.2s, border-color 0.2s",
                      }}
                    />
                  ) : (
                    <FiUser
                      style={{
                        marginRight: 10,
                        fontSize: 24,
                        color: "#10b981",
                      }}
                    />
                  )}
                  <span>{user.name}</span>
                </Link>
                <button
                  onClick={handleLogout}
                  className="nav-link logout-btn"
                  aria-label="Logout"
                  style={{
                    background: "none",
                    border: "none",
                    color: "#ef4444",
                    fontWeight: 700,
                    display: "flex",
                    alignItems: "center",
                    cursor: "pointer",
                    padding: "0.75rem 1.5rem",
                    borderRadius: "0.75rem",
                    transition: "background 0.2s, color 0.2s",
                  }}
                >
                  <FiLogOut style={{ marginRight: 8 }} /> Logout
                </button>
              </div>
            ) : (
              <div
                className="auth-links"
                style={{ display: "flex", gap: "0.5rem" }}
              >
                <Link
                  to="/login"
                  className="nav-link"
                  onClick={closeMenu}
                  tabIndex={0}
                  aria-label="Login"
                >
                  Login
                </Link>
                <Link
                  to="/register"
                  className="btn btn-primary"
                  onClick={closeMenu}
                  tabIndex={0}
                  aria-label="Sign Up"
                  style={{
                    fontWeight: 700,
                    fontSize: "1rem",
                    borderRadius: "0.75rem",
                    padding: "0.75rem 1.5rem",
                  }}
                >
                  Sign Up
                </Link>
              </div>
            )}
          </div>

          <button
            className="menu-toggle"
            aria-label={isMenuOpen ? "Close menu" : "Open menu"}
            onClick={toggleMenu}
            style={{
              background: "rgba(16,185,129,0.08)",
              border: "none",
              cursor: "pointer",
              fontSize: "2rem",
              borderRadius: "0.75rem",
              padding: "0.5rem 1rem",
              color: "#10b981",
              transition: "background 0.2s, color 0.2s",
              marginLeft: "1rem",
              boxShadow: isMenuOpen ? "0 4px 16px #10b98122" : "none",
              outline: isMenuOpen ? "2px solid #10b981" : "none",
              zIndex: 200,
            }}
          >
            {isMenuOpen ? <FiX /> : <FiMenu />}
          </button>
        </div>
      </div>
    </nav>
  )
}

export default Navbar
