import api from "./api"

// Mock data for when API is not available
const MOCK_RECIPES = [
  {
    id: 1,
    title: "Mediterranean Bowl",
    description: "A healthy and delicious Mediterranean-inspired bowl with fresh vegetables, olives, and feta cheese.",
    imageUrl: "/recipe-images/Mediterranean-Bowl-Recipe.jpg",
    prepTime: 15,
    cookTime: 20,
    servings: 4,
    calories: 320,
    difficulty: "easy",
    category: "healthy",
    tags: ["mediterranean", "healthy", "vegetarian"],
    rating: 4.5,
    ingredients: [
      "2 cups quinoa",
      "1 cucumber, diced",
      "1 cup cherry tomatoes, halved",
      "1/2 red onion, thinly sliced",
      "1/2 cup kalamata olives",
      "4 oz feta cheese, crumbled",
      "1/4 cup olive oil",
      "2 tbsp lemon juice",
      "1 tsp dried oregano",
      "Salt and pepper to taste"
    ],
    instructions: [
      "Cook quinoa according to package directions and let cool.",
      "In a large bowl, combine quinoa, cucumber, tomatoes, and red onion.",
      "In a small bowl, whisk together olive oil, lemon juice, oregano, salt, and pepper.",
      "Pour dressing over quinoa mixture and toss to combine.",
      "Top with olives and feta cheese.",
      "Serve immediately or chill for later."
    ]
  },
  {
    id: 2,
    title: "Spicy Mushroom Stir-fry",
    description: "A quick and flavorful stir-fry with mixed mushrooms and aromatic spices.",
    imageUrl: "/recipe-images/msm.jpg",
    prepTime: 10,
    cookTime: 15,
    servings: 2,
    calories: 180,
    difficulty: "easy",
    category: "vegetarian",
    tags: ["spicy", "quick", "mushrooms"],
    rating: 4.2,
    ingredients: [
      "1 lb mixed mushrooms, sliced",
      "2 tbsp vegetable oil",
      "3 cloves garlic, minced",
      "1 inch ginger, minced",
      "2 green onions, sliced",
      "2 tbsp soy sauce",
      "1 tbsp oyster sauce",
      "1 tsp chili flakes",
      "1 tsp sesame oil"
    ],
    instructions: [
      "Heat vegetable oil in a large wok or skillet over high heat.",
      "Add mushrooms and cook until golden brown, about 5-7 minutes.",
      "Add garlic and ginger, stir-fry for 30 seconds.",
      "Add soy sauce, oyster sauce, and chili flakes.",
      "Stir-fry for another 2-3 minutes.",
      "Remove from heat, add sesame oil and green onions.",
      "Serve hot with rice."
    ]
  },
  {
    id: 3,
    title: "Classic Chicken Parmesan",
    description: "Crispy breaded chicken breast topped with marinara sauce and melted cheese.",
    imageUrl: null,
    prepTime: 20,
    cookTime: 25,
    servings: 4,
    calories: 450,
    difficulty: "medium",
    category: "main",
    tags: ["chicken", "italian", "comfort-food"],
    rating: 4.7,
    ingredients: [
      "4 chicken breasts, pounded thin",
      "1 cup breadcrumbs",
      "1/2 cup parmesan cheese, grated",
      "2 eggs, beaten",
      "1 cup flour",
      "2 cups marinara sauce",
      "2 cups mozzarella cheese, shredded",
      "2 tbsp olive oil",
      "Salt and pepper to taste"
    ],
    instructions: [
      "Preheat oven to 375째F.",
      "Set up breading station with flour, beaten eggs, and breadcrumb mixture.",
      "Season chicken with salt and pepper, then bread each piece.",
      "Heat olive oil in a large skillet and brown chicken on both sides.",
      "Transfer chicken to baking dish, top with marinara and cheese.",
      "Bake for 20-25 minutes until cheese is melted and bubbly.",
      "Serve with pasta or salad."
    ]
  },
  {
    id: 4,
    title: "Chocolate Chip Cookies",
    description: "Classic homemade chocolate chip cookies that are crispy on the outside and chewy on the inside.",
    imageUrl: null,
    prepTime: 15,
    cookTime: 12,
    servings: 24,
    calories: 150,
    difficulty: "easy",
    category: "dessert",
    tags: ["cookies", "chocolate", "baking"],
    rating: 4.8,
    ingredients: [
      "2 1/4 cups all-purpose flour",
      "1 tsp baking soda",
      "1 tsp salt",
      "1 cup butter, softened",
      "3/4 cup granulated sugar",
      "3/4 cup brown sugar",
      "2 large eggs",
      "2 tsp vanilla extract",
      "2 cups chocolate chips"
    ],
    instructions: [
      "Preheat oven to 375째F.",
      "Mix flour, baking soda, and salt in a bowl.",
      "Cream butter and sugars until fluffy.",
      "Beat in eggs and vanilla.",
      "Gradually add flour mixture.",
      "Stir in chocolate chips.",
      "Drop rounded tablespoons onto ungreased baking sheets.",
      "Bake 9-11 minutes until golden brown."
    ]
  }
];

class RecipesAPI {
  // Get all recipes with optional filters
  async getRecipes(params = {}) {
    try {
      const response = await api.get("/recipes", { params })
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock data when API is not available
      console.log("API not available, using mock data")
      return {
        success: true,
        data: MOCK_RECIPES,
      }
    }
  }

  // Get a single recipe by ID
  async getRecipeById(id) {
    try {
      const response = await api.get(`/recipes/${id}`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock data when API is not available
      const recipe = MOCK_RECIPES.find(r => r.id === parseInt(id))
      if (recipe) {
        return {
          success: true,
          data: recipe,
        }
      }
      return {
        success: false,
        error: "Recipe not found",
        data: null,
      }
    }
  }

  // Search recipes
  async searchRecipes(searchTerm, filters = {}) {
    try {
      const params = {
        search: searchTerm,
        ...filters,
      }
      const response = await api.get("/recipes/search", { params })
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock data search when API is not available
      const filtered = MOCK_RECIPES.filter(recipe => 
        recipe.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        recipe.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        recipe.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      )
      return {
        success: true,
        data: filtered,
      }
    }
  }

  // Get recipes by category
  async getRecipesByCategory(category) {
    try {
      const response = await api.get(`/recipes/category/${category}`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // console.error("Error fetching recipes by category:", error)
      return {
        success: false,
        error:
          error.response?.data?.message ||
          "Failed to fetch recipes by category",
        data: [],
      }
    }
  }

  // Get featured/popular recipes
  async getFeaturedRecipes() {
    try {
      const response = await api.get("/recipes/featured")
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock data (return recipes with high ratings)
      const featured = MOCK_RECIPES.filter(recipe => recipe.rating >= 4.5)
      return {
        success: true,
        data: featured,
      }
    }
  }

  // Add recipe to favorites
  async addToFavorites(recipeId) {
    try {
      const response = await api.post(`/recipes/${recipeId}/favorite`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {

      // Fallback to localStorage
      try {
        const favorites = JSON.parse(
          localStorage.getItem("userFavorites") || "[]"
        )
        if (!favorites.includes(recipeId)) {
          favorites.push(recipeId)
          localStorage.setItem("userFavorites", JSON.stringify(favorites))
        }

        return {
          success: true,
          data: { message: "Added to favorites" },
        }
      } catch (localError) {
        console.error("Error saving to localStorage:", localError)
        return {
          success: false,
          error: "Failed to add to favorites",
        }
      }
    }
  }

  // Remove recipe from favorites
  async removeFromFavorites(recipeId) {
    try {
      const response = await api.delete(`/recipes/${recipeId}/favorite`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {

      // Fallback to localStorage
      try {
        const favorites = JSON.parse(
          localStorage.getItem("userFavorites") || "[]"
        )
        const updatedFavorites = favorites.filter((id) => id !== recipeId)
        localStorage.setItem("userFavorites", JSON.stringify(updatedFavorites))

        return {
          success: true,
          data: { message: "Removed from favorites" },
        }
      } catch (localError) {
        console.error("Error removing from localStorage:", localError)
        return {
          success: false,
          error: "Failed to remove from favorites",
        }
      }
    }
  }

  // Check if recipe is in favorites
  async isFavorite(recipeId) {
    try {
      const response = await api.get(`/recipes/${recipeId}/favorite`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {

      // Fallback to localStorage
      try {
        const favorites = JSON.parse(
          localStorage.getItem("userFavorites") || "[]"
        )
        return {
          success: true,
          data: { isFavorite: favorites.includes(recipeId) },
        }
      } catch (localError) {
        console.error("Error checking localStorage:", localError)
        return {
          success: false,
          error: "Failed to check favorite status",
        }
      }
    }
  }

  // Get all favorite recipes
  async getFavorites() {
    try {
      const response = await api.get("/recipes/favorites")
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {

      // Fallback to localStorage
      try {
        const favoriteIds = JSON.parse(
          localStorage.getItem("userFavorites") || "[]"
        )
        return {
          success: true,
          data: favoriteIds,
        }
      } catch (localError) {
        console.error("Error getting favorites from localStorage:", localError)
        return {
          success: false,
          error: "Failed to get favorites",
        }
      }
    }
  }

  // Rate a recipe
  async rateRecipe(recipeId, rating) {
    try {
      const response = await api.post(`/recipes/${recipeId}/rate`, { rating })
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      console.error("Error rating recipe:", error)
      return {
        success: false,
        error: error.response?.data?.message || "Failed to rate recipe",
      }
    }
  }

  // Get recipe categories
  async getCategories() {
    try {
      const response = await api.get("/recipes/categories")
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      console.error("Error fetching categories:", error)
      return {
        success: false,
        error: error.response?.data?.message || "Failed to fetch categories",
        data: [],
      }
    }
  }

  // Get recipe tags
  async getTags() {
    try {
      const response = await api.get("/recipes/tags")
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      console.error("Error fetching tags:", error)
      return {
        success: false,
        error: error.response?.data?.message || "Failed to fetch tags",
        data: [],
      }
    }
  }
}

const recipesAPI = new RecipesAPI()
export default recipesAPI
