import React, { useState, useEffect } from 'react'
import { useAuth } from '../contexts/Auth'
import { useNavigate } from 'react-router-dom'
import { 
  FiUser, 
  FiMail, 
  FiLock, 
  FiBell, 
  FiEye, 
  FiEyeOff, 
  FiSave, 
  FiTrash2,
  FiCamera,
  FiArrowLeft
} from 'react-icons/fi'
import '../contexts/style.css'

const Settings = () => {
  const { user, updateUser, deleteAccount } = useAuth()
  const navigate = useNavigate()

  // Form states
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    username: '',
    bio: '',
    profileImage: ''
  })

  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  })

  const [notifications, setNotifications] = useState({
    emailNotifications: true,
    recipeUpdates: true,
    mealPlanReminders: true,
    weeklyDigest: false
  })

  const [privacy, setPrivacy] = useState({
    profileVisibility: 'public',
    showEmail: false,
    showRecipeCount: true
  })

  // UI states
  const [activeTab, setActiveTab] = useState('profile')
  const [showCurrentPassword, setShowCurrentPassword] = useState(false)
  const [showNewPassword, setShowNewPassword] = useState(false)
  const [showConfirmPassword, setShowConfirmPassword] = useState(false)
  const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState({ type: '', text: '' })

  // Load user data on component mount
  useEffect(() => {
    if (user) {
      setFormData({
        firstName: user.firstName || '',
        lastName: user.lastName || '',
        email: user.email || '',
        username: user.username || '',
        bio: user.bio || '',
        profileImage: user.profileImage || ''
      })

      // Load settings from localStorage
      const savedNotifications = localStorage.getItem(`notifications_${user.id}`)
      const savedPrivacy = localStorage.getItem(`privacy_${user.id}`)

      if (savedNotifications) {
        setNotifications(JSON.parse(savedNotifications))
      }

      if (savedPrivacy) {
        setPrivacy(JSON.parse(savedPrivacy))
      }
    }
  }, [user])

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target
    setFormData(prev => ({
      ...prev,
      [name]: value
    }))
  }

  const handlePasswordChange = (e) => {
    const { name, value } = e.target
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }))
  }

  const handleNotificationChange = (setting) => {
    setNotifications(prev => ({
      ...prev,
      [setting]: !prev[setting]
    }))
  }

  const handlePrivacyChange = (setting, value) => {
    setPrivacy(prev => ({
      ...prev,
      [setting]: value
    }))
  }

  // Save profile changes
  const handleSaveProfile = async (e) => {
    e.preventDefault()
    setLoading(true)
    setMessage({ type: '', text: '' })

    try {
      await updateUser(formData)
      setMessage({ type: 'success', text: 'Profile updated successfully!' })
    } catch (error) {
      setMessage({ type: 'error', text: error.message || 'Failed to update profile' })
    } finally {
      setLoading(false)
    }
  }

  // Change password
  const handleChangePassword = async (e) => {
    e.preventDefault()
    setLoading(true)
    setMessage({ type: '', text: '' })

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setMessage({ type: 'error', text: 'New passwords do not match' })
      setLoading(false)
      return
    }

    if (passwordData.newPassword.length < 6) {
      setMessage({ type: 'error', text: 'Password must be at least 6 characters' })
      setLoading(false)
      return
    }

    try {
      // You'll need to implement this in your auth context
      // await changePassword(passwordData.currentPassword, passwordData.newPassword)
      setMessage({ type: 'success', text: 'Password changed successfully!' })
      setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' })
    } catch (error) {
      setMessage({ type: 'error', text: error.message || 'Failed to change password' })
    } finally {
      setLoading(false)
    }
  }

  // Save notification settings
  const handleSaveNotifications = () => {
    localStorage.setItem(`notifications_${user.id}`, JSON.stringify(notifications))
    setMessage({ type: 'success', text: 'Notification settings saved!' })
  }

  // Save privacy settings
  const handleSavePrivacy = () => {
    localStorage.setItem(`privacy_${user.id}`, JSON.stringify(privacy))
    setMessage({ type: 'success', text: 'Privacy settings saved!' })
  }

  // Handle profile image upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0]
    if (file) {
      const reader = new FileReader()
      reader.onload = (e) => {
        setFormData(prev => ({
          ...prev,
          profileImage: e.target.result
        }))
      }
      reader.readAsDataURL(file)
    }
  }

  // Delete account
  const handleDeleteAccount = async () => {
    if (window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      if (window.confirm('This will permanently delete all your recipes, meal plans, and data. Type "DELETE" to confirm.')) {
        const confirmation = prompt('Type "DELETE" to confirm account deletion:')
        if (confirmation === 'DELETE') {
          try {
            await deleteAccount()
            navigate('/login')
          } catch (error) {
            setMessage({ type: 'error', text: 'Failed to delete account' })
          }
        }
      }
    }
  }

  const tabs = [
    { id: 'profile', label: 'Profile', icon: FiUser },
    { id: 'security', label: 'Security', icon: FiLock },
    { id: 'notifications', label: 'Notifications', icon: FiBell },
    { id: 'privacy', label: 'Privacy', icon: FiEye }
  ]

  return (
    <div className="container" style={{ paddingTop: '2rem', paddingBottom: '2rem' }}>
      {/* Header */}
      <div className="settings-header" style={{ marginBottom: '2rem' }}>
        <button 
          onClick={() => navigate('/profile')}
          className="btn btn-outline"
          style={{ marginBottom: '1rem' }}
        >
          <FiArrowLeft style={{ marginRight: '0.5rem' }} />
          Back to Profile
        </button>
        <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: 'var(--primary-dark)' }}>
          Settings
        </h1>
        <p style={{ color: 'var(--text-color)', opacity: 0.7 }}>
          Manage your account settings and preferences
        </p>
      </div>

      {/* Message */}
      {message.text && (
        <div 
          className={`alert alert-${message.type}`}
          style={{
            padding: '1rem',
            borderRadius: '0.5rem',
            marginBottom: '2rem',
            backgroundColor: message.type === 'success' ? '#d1fae5' : '#fee2e2',
            color: message.type === 'success' ? '#065f46' : '#dc2626',
            border: `1px solid ${message.type === 'success' ? '#10b981' : '#ef4444'}`
          }}
        >
          {message.text}
        </div>
      )}

      <div className="settings-container" style={{ display: 'flex', gap: '2rem' }}>
        {/* Sidebar */}
        <div 
          className="settings-sidebar" 
          style={{ 
            minWidth: '250px',
            backgroundColor: 'white',
            borderRadius: '1rem',
            padding: '1.5rem',
            height: 'fit-content',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
          }}
        >
          <nav>
            {tabs.map(tab => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`settings-tab ${activeTab === tab.id ? 'active' : ''}`}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  width: '100%',
                  padding: '0.75rem 1rem',
                  marginBottom: '0.5rem',
                  border: 'none',
                  borderRadius: '0.5rem',
                  backgroundColor: activeTab === tab.id ? 'var(--primary)' : 'transparent',
                  color: activeTab === tab.id ? 'white' : 'var(--text-color)',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease'
                }}
              >
                <tab.icon style={{ marginRight: '0.75rem', fontSize: '1.1rem' }} />
                {tab.label}
              </button>
            ))}
          </nav>
        </div>

        {/* Content */}
        <div 
          className="settings-content"
          style={{
            flex: 1,
            backgroundColor: 'white',
            borderRadius: '1rem',
            padding: '2rem',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
          }}
        >
          {/* Profile Tab */}
          {activeTab === 'profile' && (
            <div>
              <h2 style={{ marginBottom: '1.5rem', color: 'var(--primary-dark)' }}>Profile Information</h2>

              {/* Profile Image */}
              <div style={{ marginBottom: '2rem', textAlign: 'center' }}>
                <div 
                  style={{
                    position: 'relative',
                    display: 'inline-block',
                    marginBottom: '1rem'
                  }}
                >
                  {formData.profileImage ? (
                    <img
                      src={formData.profileImage}
                      alt="Profile"
                      style={{
                        width: '120px',
                        height: '120px',
                        borderRadius: '50%',
                        objectFit: 'cover',
                        border: '4px solid var(--primary)'
                      }}
                    />
                  ) : (
                    <div
                      style={{
                        width: '120px',
                        height: '120px',
                        borderRadius: '50%',
                        backgroundColor: 'var(--primary-bg)',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        border: '4px solid var(--primary)',
                        fontSize: '3rem',
                        color: 'var(--primary)'
                      }}
                    >
                      <FiUser />
                    </div>
                  )}
                  <label 
                    htmlFor="profile-image-upload"
                    style={{
                      position: 'absolute',
                      bottom: '0',
                      right: '0',
                      backgroundColor: 'var(--primary)',
                      color: 'white',
                      borderRadius: '50%',
                      width: '36px',
                      height: '36px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      cursor: 'pointer',
                      border: '2px solid white'
                    }}
                  >
                    <FiCamera />
                  </label>
                  <input
                    id="profile-image-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    style={{ display: 'none' }}
                  />
                </div>
              </div>

              <form onSubmit={handleSaveProfile}>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
                  <div className="form-group">
                    <label htmlFor="firstName">First Name</label>
                    <input
                      type="text"
                      id="firstName"
                      name="firstName"
                      value={formData.firstName}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="lastName">Last Name</label>
                    <input
                      type="text"
                      id="lastName"
                      name="lastName"
                      value={formData.lastName}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="username">Username</label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="bio">Bio</label>
                  <textarea
                    id="bio"
                    name="bio"
                    value={formData.bio}
                    onChange={handleInputChange}
                    className="form-control"
                    rows="4"
                    placeholder="Tell us about yourself..."
                  />
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={loading}
                  style={{ display: 'flex', alignItems: 'center' }}
                >
                  <FiSave style={{ marginRight: '0.5rem' }} />
                  {loading ? 'Saving...' : 'Save Changes'}
                </button>
              </form>
            </div>
          )}

          {/* Security Tab */}
          {activeTab === 'security' && (
            <div>
              <h2 style={{ marginBottom: '1.5rem', color: 'var(--primary-dark)' }}>Security Settings</h2>

              <form onSubmit={handleChangePassword}>
                <div className="form-group">
                  <label htmlFor="currentPassword">Current Password</label>
                  <div style={{ position: 'relative' }}>
                    <input
                      type={showCurrentPassword ? 'text' : 'password'}
                      id="currentPassword"
                      name="currentPassword"
                      value={passwordData.currentPassword}
                      onChange={handlePasswordChange}
                      className="form-control"
                      required
                    />
                    <button
                      type="button"
                      onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                      style={{
                        position: 'absolute',
                        right: '10px',
                        top: '50%',
                        transform: 'translateY(-50%)',
                        border: 'none',
                        background: 'none',
                        cursor: 'pointer'
                      }}
                    >
                      {showCurrentPassword ? <FiEyeOff /> : <FiEye />}
                    </button>
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="newPassword">New Password</label>
                  <div style={{ position: 'relative' }}>
                    <input
                      type={showNewPassword ? 'text' : 'password'}
                      id="newPassword"
                      name="newPassword"
                      value={passwordData.newPassword}
                      onChange={handlePasswordChange}
                      className="form-control"
                      minLength="6"
                      required
                    />
                    <button
                      type="button"
                      onClick={() => setShowNewPassword(!showNewPassword)}
                      style={{
                        position: 'absolute',
                        right: '10px',
                        top: '50%',
                        transform: 'translateY(-50%)',
                        border: 'none',
                        background: 'none',
                        cursor: 'pointer'
                      }}
                    >
                      {showNewPassword ? <FiEyeOff /> : <FiEye />}
                    </button>
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="confirmPassword">Confirm New Password</label>
                  <div style={{ position: 'relative' }}>
                    <input
                      type={showConfirmPassword ? 'text' : 'password'}
                      id="confirmPassword"
                      name="confirmPassword"
                      value={passwordData.confirmPassword}
                      onChange={handlePasswordChange}
                      className="form-control"
                      minLength="6"
                      required
                    />
                    <button
                      type="button"
                      onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                      style={{
                        position: 'absolute',
                        right: '10px',
                        top: '50%',
                        transform: 'translateY(-50%)',
                        border: 'none',
                        background: 'none',
                        cursor: 'pointer'
                      }}
                    >
                      {showConfirmPassword ? <FiEyeOff /> : <FiEye />}
                    </button>
                  </div>
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={loading}
                  style={{ display: 'flex', alignItems: 'center', marginBottom: '2rem' }}
                >
                  <FiLock style={{ marginRight: '0.5rem' }} />
                  {loading ? 'Changing...' : 'Change Password'}
                </button>
              </form>

              {/* Danger Zone */}
              <div 
                style={{
                  borderTop: '1px solid #e5e7eb',
                  paddingTop: '2rem',
                  marginTop: '2rem'
                }}
              >
                <h3 style={{ color: '#ef4444', marginBottom: '1rem' }}>Danger Zone</h3>
                <p style={{ marginBottom: '1rem', color: '#6b7280' }}>
                  Once you delete your account, there is no going back. Please be certain.
                </p>
                <button 
                  onClick={handleDeleteAccount}
                  className="btn btn-danger"
                  style={{ display: 'flex', alignItems: 'center' }}
                >
                  <FiTrash2 style={{ marginRight: '0.5rem' }} />
                  Delete Account
                </button>
              </div>
            </div>
          )}

          {/* Notifications Tab */}
          {activeTab === 'notifications' && (
            <div>
              <h2 style={{ marginBottom: '1.5rem', color: 'var(--primary-dark)' }}>Notification Preferences</h2>

              <div style={{ marginBottom: '2rem' }}>
                {Object.entries(notifications).map(([key, value]) => (
                  <div 
                    key={key}
                    style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      padding: '1rem 0',
                      borderBottom: '1px solid #e5e7eb'
                    }}
                  >
                    <div>
                      <h4 style={{ margin: 0, marginBottom: '0.25rem' }}>
                        {key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                      </h4>
                      <p style={{ margin: 0, fontSize: '0.875rem', color: '#6b7280' }}>
                        {key === 'emailNotifications' && 'Receive email notifications'}
                        {key === 'recipeUpdates' && 'Get notified when recipes are updated'}
                        {key === 'mealPlanReminders' && 'Receive meal planning reminders'}
                        {key === 'weeklyDigest' && 'Weekly summary of your activity'}
                      </p>
                    </div>
                    <label className="switch">
                      <input
                        type="checkbox"
                        checked={value}
                        onChange={() => handleNotificationChange(key)}
                      />
                      <span className="slider round"></span>
                    </label>
                  </div>
                ))}
              </div>

              <button 
                onClick={handleSaveNotifications}
                className="btn btn-primary"
                style={{ display: 'flex', alignItems: 'center' }}
              >
                <FiSave style={{ marginRight: '0.5rem' }} />
                Save Preferences
              </button>
            </div>
          )}

          {/* Privacy Tab */}
          {activeTab === 'privacy' && (
            <div>
              <h2 style={{ marginBottom: '1.5rem', color: 'var(--primary-dark)' }}>Privacy Settings</h2>

              <div style={{ marginBottom: '2rem' }}>
                <div className="form-group">
                  <label htmlFor="profileVisibility">Profile Visibility</label>
                  <select
                    id="profileVisibility"
                    value={privacy.profileVisibility}
                    onChange={(e) => handlePrivacyChange('profileVisibility', e.target.value)}
                    className="form-control"
                  >
                    <option value="public">Public</option>
                    <option value="private">Private</option>
                    <option value="friends">Friends Only</option>
                  </select>
                </div>

                <div 
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '1rem 0',
                    borderBottom: '1px solid #e5e7eb'
                  }}
                >
                  <div>
                    <h4 style={{ margin: 0, marginBottom: '0.25rem' }}>Show Email Address</h4>
                    <p style={{ margin: 0, fontSize: '0.875rem', color: '#6b7280' }}>
                      Display your email on your public profile
                    </p>
                  </div>
                  <label className="switch">
                    <input
                      type="checkbox"
                      checked={privacy.showEmail}
                      onChange={(e) => handlePrivacyChange('showEmail', e.target.checked)}
                    />
                    <span className="slider round"></span>
                  </label>
                </div>

                <div 
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '1rem 0',
                    borderBottom: '1px solid #e5e7eb'
                  }}
                >
                  <div>
                    <h4 style={{ margin: 0, marginBottom: '0.25rem' }}>Show Recipe Count</h4>
                    <p style={{ margin: 0, fontSize: '0.875rem', color: '#6b7280' }}>
                      Display the number of recipes you've created
                    </p>
                  </div>
                  <label className="switch">
                    <input
                      type="checkbox"
                      checked={privacy.showRecipeCount}
                      onChange={(e) => handlePrivacyChange('showRecipeCount', e.target.checked)}
                    />
                    <span className="slider round"></span>
                  </label>
                </div>
              </div>

              <button 
                onClick={handleSavePrivacy}
                className="btn btn-primary"
                style={{ display: 'flex', alignItems: 'center' }}
              >
                <FiSave style={{ marginRight: '0.5rem' }} />
                Save Privacy Settings
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default Settings
