import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FiSearch, FiFilter, FiClock, FiUsers, FiHeart } from 'react-icons/fi';
import './Recipes.css';

const Recipes = () => {
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [maxCalories, setMaxCalories] = useState(1000);
  const [showFilters, setShowFilters] = useState(false);

  const categories = [
    { value: 'all', label: 'All Recipes' },
    { value: 'breakfast', label: 'Breakfast' },
    { value: 'lunch', label: 'Lunch' },
    { value: 'dinner', label: 'Dinner' },
    { value: 'snack', label: 'Snacks' },
    { value: 'dessert', label: 'Healthy Desserts' }
  ];

  // Sample recipes data (in real app, this would come from API)
  const sampleRecipes = [
    {
      id: 1,
      title: 'Mediterranean Quinoa Bowl',
      description: 'A nutritious bowl packed with quinoa, fresh vegetables, and Mediterranean flavors.',
      image: 'ü•ó',
      calories: 320,
      cookingTime: 25,
      servings: 2,
      category: 'lunch',
      tags: ['vegetarian', 'high-protein', 'mediterranean'],
      difficulty: 'Easy',
      rating: 4.8
    },
    {
      id: 2,
      title: 'Grilled Salmon with Avocado Salsa',
      description: 'Omega-3 rich salmon paired with fresh avocado salsa for a heart-healthy meal.',
      image: 'üêü',
      calories: 380,
      cookingTime: 20,
      servings: 4,
      category: 'dinner',
      tags: ['keto', 'high-protein', 'omega-3'],
      difficulty: 'Medium',
      rating: 4.9
    },
    {
      id: 3,
      title: 'Green Smoothie Power Bowl',
      description: 'Energizing smoothie bowl with spinach, banana, and superfood toppings.',
      image: 'ü•§',
      calories: 280,
      cookingTime: 10,
      servings: 1,
      category: 'breakfast',
      tags: ['vegan', 'smoothie', 'antioxidants'],
      difficulty: 'Easy',
      rating: 4.7
    },
    {
      id: 4,
      title: 'Zucchini Noodles with Pesto',
      description: 'Low-carb alternative to pasta with homemade basil pesto.',
      image: 'üçù',
      calories: 195,
      cookingTime: 15,
      servings: 2,
      category: 'lunch',
      tags: ['low-carb', 'vegetarian', 'gluten-free'],
      difficulty: 'Easy',
      rating: 4.6
    },
    {
      id: 5,
      title: 'Chia Seed Pudding',
      description: 'Creamy overnight pudding rich in omega-3s and fiber.',
      image: 'üçÆ',
      calories: 220,
      cookingTime: 5,
      servings: 1,
      category: 'dessert',
      tags: ['vegan', 'make-ahead', 'fiber-rich'],
      difficulty: 'Easy',
      rating: 4.5
    },
    {
      id: 6,
      title: 'Turkey and Vegetable Lettuce Wraps',
      description: 'Light and fresh wraps packed with lean protein and crunchy vegetables.',
      image: 'ü•¨',
      calories: 240,
      cookingTime: 20,
      servings: 4,
      category: 'lunch',
      tags: ['low-carb', 'high-protein', 'gluten-free'],
      difficulty: 'Easy',
      rating: 4.4
    }
  ];

  useEffect(() => {
    // Simulate API call
    const fetchRecipes = async () => {
      try {
        setLoading(true);
        // In a real app, you would make an API call here
        // const response = await axios.get('/api/recipes');
        // setRecipes(response.data);

        // For demo purposes, use sample data
        setTimeout(() => {
          setRecipes(sampleRecipes);
          setFilteredRecipes(sampleRecipes);
          setLoading(false);
        }, 1000);
      } catch (error) {
        console.error('Error fetching recipes:', error);
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  useEffect(() => {
    let filtered = recipes;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(recipe =>
        recipe.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        recipe.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        recipe.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(recipe => recipe.category === selectedCategory);
    }

    // Filter by calories
    filtered = filtered.filter(recipe => recipe.calories <= maxCalories);

    setFilteredRecipes(filtered);
  }, [recipes, searchTerm, selectedCategory, maxCalories]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleCaloriesChange = (e) => {
    setMaxCalories(parseInt(e.target.value));
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading delicious recipes...</p>
      </div>
    );
  }

  return (
    <div className="recipes-page">
      <div className="container">
        <div className="page-header">
          <h1>Healthy Recipes</h1>
          <p>Discover nutritious and delicious recipes for your weight loss journey</p>
        </div>

        {/* Search and Filters */}
        <div className="recipes-controls">
          <div className="search-bar">
            <FiSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search recipes, ingredients, or tags..."
              value={searchTerm}
              onChange={handleSearchChange}
              className="search-input"
            />
          </div>

          <button 
            className="filter-toggle"
            onClick={() => setShowFilters(!showFilters)}
          >
            <FiFilter /> Filters
          </button>
        </div>

        {/* Filters Panel */}
        {showFilters && (
          <div className="filters-panel">
            <div className="filter-group">
              <label>Category</label>
              <div className="category-filters">
                {categories.map(category => (
                  <button
                    key={category.value}
                    className={`category-btn ${selectedCategory === category.value ? 'active' : ''}`}
                    onClick={() => handleCategoryChange(category.value)}
                  >
                    {category.label}
                  </button>
                ))}
              </div>
            </div>

            <div className="filter-group">
              <label>Max Calories: {maxCalories}</label>
              <input
                type="range"
                min="100"
                max="800"
                value={maxCalories}
                onChange={handleCaloriesChange}
                className="calories-slider"
              />
            </div>
          </div>
        )}

        {/* Results Count */}
        <div className="results-info">
          <p>{filteredRecipes.length} recipes found</p>
        </div>

        {/* Recipes Grid */}
        <div className="recipes-grid">
          {filteredRecipes.map(recipe => (
            <Link to={`/recipes/${recipe.id}`} key={recipe.id} className="recipe-card">
              <div className="recipe-image">
                <span className="recipe-emoji">{recipe.image}</span>
                <div className="recipe-overlay">
                  <button className="favorite-btn">
                    <FiHeart />
                  </button>
                </div>
              </div>

              <div className="recipe-content">
                <h3 className="recipe-title">{recipe.title}</h3>
                <p className="recipe-description">{recipe.description}</p>

                <div className="recipe-meta">
                  <div className="meta-item">
                    <FiClock />
                    <span>{recipe.cookingTime}min</span>
                  </div>
                  <div className="meta-item">
                    <FiUsers />
                    <span>{recipe.servings} servings</span>
                  </div>
                  <div className="meta-item calories">
                    <span>{recipe.calories} cal</span>
                  </div>
                </div>

                <div className="recipe-tags">
                  {recipe.tags.slice(0, 3).map(tag => (
                    <span key={tag} className="tag">{tag}</span>
                  ))}
                </div>

                <div className="recipe-footer">
                  <div className="difficulty">
                    <span className={`difficulty-badge ${recipe.difficulty.toLowerCase()}`}>
                      {recipe.difficulty}
                    </span>
                  </div>
                  <div className="rating">
                    <span>‚≠ê {recipe.rating}</span>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>

        {filteredRecipes.length === 0 && (
          <div className="no-results">
            <h3>No recipes found</h3>
            <p>Try adjusting your search terms or filters</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Recipes;
