import React, { useState, useEffect } from "react"
import { useAuth } from "../contexts/Auth"
import recipesAPI from "../services/recipesAPI"
import { FiHeart, FiEdit3, FiUser, FiSave, FiX } from "react-icons/fi"

const Profile = () => {
  const { user, updateUser } = useAuth()
  const [activeTab, setActiveTab] = useState("favorites")
  const [userStats, setUserStats] = useState({
    favoriteRecipes: [],
    weightProgress: [],
    totalMealsLogged: 156,
    averageCalories: 1450,
    currentWeight: 165,
    targetWeight: 155,
    startingWeight: 180,
  })
  const [profileData, setProfileData] = useState({
    firstName: user?.firstName || "",
    lastName: user?.lastName || "",
    email: user?.email || "",
    username: user?.username || "",
    bio: user?.bio || "",
    profileImage: user?.profileImage || null,
  })
  const [isEditing, setIsEditing] = useState(false)
  const [saveSuccess, setSaveSuccess] = useState(false)
  const [saveError, setSaveError] = useState("")
  const [imagePreview, setImagePreview] = useState(null)

  useEffect(() => {
    // Initialize profile data when user changes
    if (user) {
      setProfileData({
        firstName: user.firstName || "",
        lastName: user.lastName || "",
        email: user.email || "",
        username: user.username || "",
        bio: user.bio || "",
        profileImage: user.profileImage || null,
      })

      if (user.profileImage) {
        setImagePreview(user.profileImage)
      }
    }

    // Load favorites from backend
    const fetchFavorites = async () => {
      try {
        const result = await recipesAPI.getFavorites()
        if (result.success) {
          setUserStats((prev) => ({
            ...prev,
            favoriteRecipes: result.data || [],
          }))
        }
      } catch (error) {
        console.error("Error fetching favorites:", error)
        // Fallback to empty array
        setUserStats((prev) => ({
          ...prev,
          favoriteRecipes: [],
        }))
      }
    }

    if (user) {
      fetchFavorites()
    }
  }, [user])

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setProfileData(prev => ({
      ...prev,
      [name]: value
    }))
  }

  const handleSaveProfile = async () => {
    try {
      setSaveError("")
      setSaveSuccess(false)

      const result = await updateUser(profileData)

      if (result.success) {
        setSaveSuccess(true)
        setIsEditing(false)
        setTimeout(() => setSaveSuccess(false), 3000)
      }
    } catch (error) {
      setSaveError(error.message || "Failed to update profile")
    }
  }

  const handleCancelEdit = () => {
    // Reset form data to original user data
    if (user) {
      setProfileData({
        firstName: user.firstName || "",
        lastName: user.lastName || "",
        email: user.email || "",
        username: user.username || "",
        bio: user.bio || "",
        profileImage: user.profileImage || null,
      })
      setImagePreview(user.profileImage || null)
    }
    setIsEditing(false)
    setSaveError("")
  }

  const handleProfileUpdate = (e) => {
    e.preventDefault()
    setIsEditing(true)

    try {
      // Save to localStorage
      const profileToSave = {
        ...profileData,
        lastUpdated: new Date().toISOString(),
      }
      localStorage.setItem(`profile_${user?.id}`, JSON.stringify(profileToSave))

      // In a real app, you would make an API call to update the profile

      // Show success message
      setSaveSuccess(true)
      setTimeout(() => setSaveSuccess(false), 3000)
    } catch (error) {
      console.error("Error saving profile:", error)
    } finally {
      setIsEditing(false)
    }
  }

  const handleImageUpload = (e) => {
    const file = e.target.files[0]
    if (file) {
      // Check file size (limit to 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert("Image size must be less than 5MB")
        return
      }

      // Check file type
      if (!file.type.startsWith("image/")) {
        alert("Please select a valid image file")
        return
      }

      const reader = new FileReader()
      reader.onload = (e) => {
        const imageData = e.target.result
        setImagePreview(imageData)
        setProfileData((prev) => ({
          ...prev,
          profileImage: imageData,
        }))
      }
      reader.readAsDataURL(file)
    }
  }

  const removeProfileImage = () => {
    setImagePreview(null)
    setProfileData((prev) => ({
      ...prev,
      profileImage: null,
    }))
  }

  const tabs = [{ id: "favorites", label: "Favorites", icon: <FiHeart /> }]

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="profile-avatar">
            {imagePreview || profileData.profileImage ? (
              <img
                src={imagePreview || profileData.profileImage}
                alt="Profile"
                className="avatar-image"
                style={{
                  width: "80px",
                  height: "80px",
                  borderRadius: "50%",
                  objectFit: "cover",
                  border: "3px solid #4caf50",
                }}
              />
            ) : (
              <span className="avatar-icon">👤</span>
            )}
          </div>
          <div className="profile-info">
            <h1>{user?.name || "User"}</h1>
            <p>{user?.email || "No email"}</p>
          </div>
        </div>

        <div className="profile-tabs">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`tab-button ${activeTab === tab.id ? "active" : ""}`}
            >
              {tab.icon}
              <span>{tab.label}</span>
            </button>
          ))}
        </div>

        <div className="profile-content">
          {activeTab === "favorites" && (
            <div className="favorites-tab">
              <div className="tab-header">
                <h3>Your Favorite Recipes</h3>
                <p>Recipes you've saved for quick access</p>
              </div>

              {!user ? (
                <div
                  className="no-favorites"
                  style={{ textAlign: "center", padding: "40px 20px" }}
                >
                  <p style={{ fontSize: "18px", marginBottom: "20px" }}>
                    Please log in to view your favorite recipes.
                  </p>
                  <button
                    className="btn btn-primary"
                    onClick={() => (window.location.href = "/login")}
                    style={{
                      padding: "12px 24px",
                      fontSize: "16px",
                      borderRadius: "6px",
                      border: "none",
                      backgroundColor: "#4caf50",
                      color: "white",
                      cursor: "pointer",
                    }}
                  >
                    Go to Login
                  </button>
                </div>
              ) : (
                <div className="favorites-grid">
                  {userStats.favoriteRecipes.length > 0 ? (
                    userStats.favoriteRecipes.map((recipe) => (
                      <div
                        key={recipe._id || recipe.id}
                        className="favorite-card"
                      >
                        <div className="favorite-image">
                          {recipe.image ? (
                            <img
                              src={recipe.image}
                              alt={recipe.title}
                              className="recipe-image"
                              style={{
                                width: "100%",
                                height: "200px",
                                objectFit: "cover",
                                borderRadius: "8px",
                              }}
                            />
                          ) : (
                            <div
                              className="recipe-placeholder"
                              style={{
                                width: "100%",
                                height: "200px",
                                backgroundColor: "#e5e7eb",
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                borderRadius: "8px",
                                color: "#6b7280",
                              }}
                            >
                              No Image
                            </div>
                          )}
                        </div>
                        <div className="favorite-content">
                          <h4>{recipe.title}</h4>
                          <p>
                            {recipe.nutrition?.calories || recipe.calories || 0}{" "}
                            calories
                          </p>
                          <p>
                            {recipe.cookTime || recipe.cookingTime || 0} min
                          </p>
                          <button
                            className="btn btn-outline btn-sm"
                            onClick={() =>
                              (window.location.href = `/recipe/${
                                recipe._id || recipe.id
                              }`)
                            }
                          >
                            View Recipe
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div
                      className="no-favorites"
                      style={{
                        textAlign: "center",
                        padding: "60px 20px",
                        color: "#6b7280",
                        backgroundColor: "#f9fafb",
                        borderRadius: "12px",
                        border: "2px dashed #d1d5db",
                      }}
                    >
                      <div style={{ fontSize: "48px", marginBottom: "16px" }}>
