import React, { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/Auth"
import { FiUser, FiLogOut, FiHome, FiBook, FiCalendar, FiChevronDown, FiSettings, FiPlusCircle } from "react-icons/fi"
import "../contexts/style.css"

const Navbar = () => {
  const { user, logout } = useAuth()
  const navigate = useNavigate()
  const [dropdownOpen, setDropdownOpen] = useState(false)

  // Handle logout properly
  const handleLogout = async () => {
    try {
      await logout()
      navigate('/login')
    } catch (error) {
      console.error('Logout error:', error)
    }
  }

  // Get user profile image from localStorage
  const getUserProfileImage = () => {
    if (!user?.id) return null
    const savedProfile = localStorage.getItem(`profile_${user.id}`)
    if (savedProfile) {
      const parsedProfile = JSON.parse(savedProfile)
      return parsedProfile.profileImage
    }
    return null
  }

  // Handle profile image click
  const handleProfileClick = () => {
    console.log('Profile clicked, navigating to profile...')
    setDropdownOpen(false)
    navigate('/profile')
  }

  // Handle dropdown toggle
  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen)
  }

  // Close dropdown when clicking outside
  const closeDropdown = () => {
    setDropdownOpen(false)
  }

  const profileImage = getUserProfileImage()

  return (
    <nav className="navbar glassmorphism" aria-label="Main Navigation">
      <div className="container">
        <div className="navbar-content">
          <Link
            to="/"
            className="navbar-brand"
            tabIndex={0}
            aria-label="Fantastic Broccoli Home"
          >
            <span className="brand-icon">ðŸ¥¦</span>
            <span className="brand-title">FantasticBroccoli</span>
          </Link>

          {/* Grouped main navigation links */}
          <nav className="main-nav-links" aria-label="Primary">
            <ul className="navbar-links">
              <li>
                <Link
                  to="/"
                  className="btn btn-outline"
                  tabIndex={0}
                  aria-label="Home"
                >
                  <FiHome style={{ marginRight: 6 }} /> Home
                </Link>
              </li>
              <li>
                <Link
                  to="/recipes"
                  className="btn btn-outline"
                  tabIndex={0}
                  aria-label="Recipes"
                >
                  <FiBook style={{ marginRight: 6 }} /> Recipes
                </Link>
              </li>
              {user && (
                <>
                  <li>
                    <Link
                      to="/meal-planner"
                      className="btn btn-outline"
                      tabIndex={0}
                      aria-label="Meal Planner"
                    >
                      <FiCalendar style={{ marginRight: 6 }} /> Meal Planner
                    </Link>
                  </li>
                  <li>
                    <Link
                      to="/create-recipe"
                      className="btn btn-outline"
                      tabIndex={0}
                      aria-label="Create Recipe"
                    >
                      <FiPlusCircle style={{ marginRight: 6 }} /> Create Recipe
                    </Link>
                  </li>
                </>
              )}
            </ul>
          </nav>

          <div className="navbar-auth">
            {user ? (
              <div className="user-menu" onBlur={closeDropdown}>
                <div className="dropdown-container">
                  <button
                    className="profile-button"
                    onClick={toggleDropdown}
                    aria-expanded={dropdownOpen}
                    aria-haspopup="true"
                  >
                    {profileImage ? (
                      <img
                        src={profileImage}
                        alt="Profile"
                        className="navbar-avatar"
                        onError={(e) => {
                          e.target.style.display = 'none'
                          e.target.nextElementSibling.style.display = 'inline-block'
                        }}
                      />
                    ) : (
                      <FiUser
                        style={{
                          marginRight: 8,
                          fontSize: 20,
                          color: "#10b981",
                        }}
                      />
                    )}
                    <span className="username">
                      {user.fullName ||
                        `${user.firstName} ${user.lastName}` ||
                        user.username}
                    </span>
                    <FiChevronDown
                      style={{
                        marginLeft: 8,
                        fontSize: 16,
                        transform: dropdownOpen ? 'rotate(180deg)' : 'rotate(0deg)',
                        transition: 'transform 0.2s ease'
                      }}
                    />
                  </button>

                  {dropdownOpen && (
                    <div className="dropdown-menu">
                      <button
                        onClick={handleProfileClick}
                        className="dropdown-item"
                        aria-label="View Profile"
                      >
                        <FiUser style={{ marginRight: 8 }} />
                        My Profile
                      </button>
                      <Link
                        to="/settings"
                        className="dropdown-item"
                        onClick={closeDropdown}
                        aria-label="Settings"
                      >
                        <FiSettings style={{ marginRight: 8 }} />
                        Settings
                      </Link>
                      <div className="dropdown-divider"></div>
                      <button
                        onClick={handleLogout}
                        className="dropdown-item logout-item"
                        aria-label="Logout"
                      >
                        <FiLogOut style={{ marginRight: 8 }} />
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <div className="auth-links">
                <Link
                  to="/register"
                  className="btn btn-outline"
                  tabIndex={0}
                  aria-label="Register"
                >
                  Register
                </Link>
                <Link
                  to="/login"
                  className="btn btn-primary"
                  tabIndex={0}
                  aria-label="Sign In"
                >
                  Sign In
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  )
}

export default Navbar
