import React, { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { FiClock, FiUsers } from "react-icons/fi"
import { useRecipes } from "../hooks/useRecipes"

const Recipes = () => {
  const { recipes, loading, error, fetchRecipes, clearError } = useRecipes()

  const [filteredRecipes, setFilteredRecipes] = useState([])
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedCategory, setSelectedCategory] = useState("all")
  const [maxCalories, setMaxCalories] = useState(1000)
  const [recipesToShow, setRecipesToShow] = useState(12)

  const categories = [
    { value: "all", label: "All Recipes" },
    { value: "breakfast", label: "Breakfast" },
    { value: "lunch", label: "Lunch" },
    { value: "dinner", label: "Dinner" },
    { value: "snack", label: "Snacks" },
    { value: "dessert", label: "Healthy Desserts" },
  ]

  useEffect(() => {
    // Fetch recipes when component mounts
    fetchRecipes()
  }, [fetchRecipes])

  useEffect(() => {
    let filtered = recipes

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(
        (recipe) =>
          recipe.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          recipe.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
          recipe.tags.some((tag) =>
            tag.toLowerCase().includes(searchTerm.toLowerCase())
          )
      )
    }

    // Filter by category
    if (selectedCategory !== "all") {
      filtered = filtered.filter(
        (recipe) => recipe.category === selectedCategory
      )
    }

    // Filter by calories
    filtered = filtered.filter((recipe) => recipe.calories <= maxCalories)

    setFilteredRecipes(filtered)
  }, [recipes, searchTerm, selectedCategory, maxCalories])

  const handleCategoryChange = (category) => {
    setSelectedCategory(category)
  }

  const handleCaloriesChange = (e) => {
    setMaxCalories(parseInt(e.target.value))
  }

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading delicious recipes...</p>
      </div>
    )
  }

  return (
    <div className="recipes-page glassmorphism-bg">
      <div className="container">
        {error && (
          <div className="error-banner">
            <p>⚠️ {error}</p>
            <button onClick={clearError} className="close-error">
              ×
            </button>
          </div>
        )}

        <div className="page-header glassmorphism" style={{ display: 'flex', alignItems: 'center', gap: '1.2rem', justifyContent: 'center', flexWrap: 'wrap' }}>
          <span style={{ fontSize: '2.5rem', lineHeight: 1, marginRight: '0.5rem', fontFamily: 'Nunito, Inter, sans-serif' }}>🥦</span>
          <h1 className="page-title" style={{ fontFamily: 'Nunito, Inter, sans-serif', fontWeight: 900, fontSize: '2.3rem', letterSpacing: '0.01em', margin: 0, color: '#059669', textShadow: '0 2px 8px #10b98122, 0 1px 0 #fff' }}>
            Fantastic Broccoli
          </h1>
        </div>

        {/* Filters Panel */}
        {/* {showFilters && (
          <div className="filters-panel glassmorphism">
            <div className="filter-group">
              <label>Category</label>
              <div className="category-filters">
                {categories.map((category) => (
                  <button
                    key={category.value}
                    className={`category-btn btn btn-outline${
                      selectedCategory === category.value ? " active" : ""
                    }`}
                    onClick={() => handleCategoryChange(category.value)}
                  >
                    {category.label}
                  </button>
                ))}
              </div>
            </div>

            <div className="filter-group">
              <label>
                Max Calories: <span className="highlight">{maxCalories}</span>
              </label>
              <input
                type="range"
                min="100"
                max="800"
                value={maxCalories}
                onChange={handleCaloriesChange}
                className="calories-slider"
              />
            </div>
          </div>
        )} */}

        {/* Recipes Grid */}
        <div className="recipes-grid modern-grid">
          {filteredRecipes.slice(0, recipesToShow).map((recipe) => (
            <Link
              to={`/recipes/${recipe.id}`}
              key={recipe.id}
              className="recipe-card glassmorphism"
            >
              <div className="recipe-image">
                {recipe.image && recipe.image.startsWith("/recipe-images/") ? (
                  <img
                    src={recipe.image}
                    alt={recipe.title}
                    className="recipe-img"
                    loading="lazy"
                  />
                ) : (
                  <span className="recipe-emoji">{recipe.image}</span>
                )}
              </div>

              <div className="recipe-content">
                <h3 className="recipe-title highlight">{recipe.title}</h3>
                <p className="recipe-description">{recipe.description}</p>

                <div className="recipe-meta">
                  <div className="meta-item">
                    <FiClock />
                    <span>{recipe.cookingTime}min</span>
                  </div>
                  <div className="meta-item">
                    <FiUsers />
                    <span>{recipe.servings} servings</span>
                  </div>
                  <div className="meta-item calories">
                    <span>{recipe.calories} cal</span>
                  </div>
                </div>

                <div className="recipe-tags">
                  {recipe.tags.slice(0, 3).map((tag) => (
                    <span key={tag} className="tag">
                      {tag}
                    </span>
                  ))}
                </div>

                <div className="recipe-footer">
                  <div className="difficulty">
                    <span
                      className={`difficulty-badge ${recipe.difficulty.toLowerCase()}`}
                    >
                      {recipe.difficulty}
                    </span>
                  </div>
                  <div className="rating">
                    <span>⭐ {recipe.rating}</span>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>

        {recipesToShow < filteredRecipes.length && (
          <div style={{ textAlign: "center", margin: "2rem 0" }}>
            <button
              className="btn btn-outline"
              onClick={() => setRecipesToShow((prev) => prev + 12)}
            >
              Show More
            </button>
          </div>
        )}

        {filteredRecipes.length === 0 && (
          <div className="no-results glassmorphism">
            <h3>No recipes found</h3>
            <p>Try adjusting your search terms or filters</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default Recipes
