import React from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/Auth"
import { FiUser, FiLogOut, FiHome, FiBook, FiCalendar } from "react-icons/fi"

const Navbar = () => {
  const { user, logout } = useAuth()
  const navigate = useNavigate()

  // Get user profile image from localStorage
  const getUserProfileImage = () => {
    if (!user?.id) return null
    const savedProfile = localStorage.getItem(`profile_${user.id}`)
    if (savedProfile) {
      const parsedProfile = JSON.parse(savedProfile)
      return parsedProfile.profileImage
    }
    return null
  }

  const profileImage = getUserProfileImage()

  const handleLogout = () => {
    logout()
    navigate("/")
  }

  return (
    <nav className="navbar glassmorphism" aria-label="Main Navigation">
      <div className="container">
        <div className="navbar-content">
          <Link
            to="/"
            className="navbar-brand"
            tabIndex={0}
            aria-label="Fantastic Broccoli Home"
          >
            <span
              className="brand-icon"
              style={{
                fontSize: "2.2rem",
                marginRight: "0.5rem",
                filter: "drop-shadow(0 2px 8px #a78bfa44)",
                background: "rgba(255,255,255,0.7)",
                borderRadius: "1rem",
                padding: "0.2rem 0.6rem",
                boxShadow: "0 2px 8px #a78bfa22",
              }}
            >
              ðŸ¥¦
            </span>
            <span className="brand-title">FantasticBroccoli</span>
          </Link>

          {/* Grouped main navigation links */}
          <nav className="main-nav-links" aria-label="Primary">
            <ul
              className="navbar-links"
              style={{
                listStyle: "none",
                display: "flex",
                gap: "0.5rem",
                margin: 0,
                padding: 0,
                background: "rgba(255,255,255,0.45)",
                borderRadius: "1rem",
                boxShadow: "0 2px 8px #10b98111",
                backdropFilter: "blur(8px)",
                alignItems: "center",
                flexDirection: "row",
              }}
            >
              <li>
                <Link
                  to="/"
                  className="btn btn-outline"
                  tabIndex={0}
                  aria-label="Home"
                  style={{
                    fontWeight: 600,
                    fontSize: "1rem",
                    borderRadius: "0.75rem",
                    padding: "0.75rem 1.5rem",
                  }}
                >
                  <FiHome style={{ marginRight: 6 }} /> Home
                </Link>
              </li>
              <li>
                <Link
                  to="/recipes"
                  className="btn btn-outline"
                  tabIndex={0}
                  aria-label="Recipes"
                  style={{
                    fontWeight: 600,
                    fontSize: "1rem",
                    borderRadius: "0.75rem",
                    padding: "0.75rem 1.5rem",
                  }}
                >
                  <FiBook style={{ marginRight: 6 }} /> Recipes
                </Link>
              </li>
              {user && (
                <li>
                  <Link
                    to="/meal-planner"
                    className="btn btn-outline"
                    tabIndex={0}
                    aria-label="Meal Planner"
                    style={{
                      fontWeight: 600,
                      fontSize: "1rem",
                      borderRadius: "0.75rem",
                      padding: "0.75rem 1.5rem",
                    }}
                  >
                    <FiCalendar style={{ marginRight: 6 }} /> Meal Planner
                  </Link>
                </li>
              )}
            </ul>
          </nav>

          <div
            className="navbar-auth"
            style={{
              display: "flex",
              alignItems: "center",
              gap: "1.5rem",
            }}
          >
            {user ? (
              <div
                className="user-menu"
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "1rem",
                }}
              >
                <Link
                  to="/profile"
                  className="nav-link profile-link"
                  tabIndex={0}
                  aria-label="Profile"
                  style={{
                    display: "flex",
                    alignItems: "center",
                    fontWeight: 700,
                  }}
                >
                  {profileImage ? (
                    <img
                      src={profileImage}
                      alt="Profile"
                      className="navbar-avatar"
                      style={{
                        width: 36,
                        height: 36,
                        borderRadius: "50%",
                        objectFit: "cover",
                        marginRight: 10,
                        border: "2.5px solid #10b981",
                        boxShadow: "0 2px 8px #10b98111",
                        transition: "box-shadow 0.2s, border-color 0.2s",
                      }}
                    />
                  ) : (
                    <FiUser
                      style={{
                        marginRight: 10,
                        fontSize: 24,
                        color: "#10b981",
                      }}
                    />
                  )}
                  <span>{user.name}</span>
                </Link>
                <button
                  onClick={handleLogout}
                  className="nav-link logout-btn"
                  aria-label="Logout"
                  style={{
                    background: "none",
                    border: "none",
                    color: "#ef4444",
                    fontWeight: 700,
                    display: "flex",
                    alignItems: "center",
                    cursor: "pointer",
                    padding: "0.75rem 1.5rem",
                    borderRadius: "0.75rem",
                    transition: "background 0.2s, color 0.2s",
                  }}
                >
                  <FiLogOut style={{ marginRight: 8 }} /> Logout
                </button>
              </div>
            ) : (
              <div
                className="auth-links"
                style={{ display: "flex", gap: "0.5rem" }}
              >
                {/* Removed duplicate Login link */}
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  )
}

export default Navbar
