import React, { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/Auth"
import { FiMail, FiLock, FiEye, FiEyeOff } from "react-icons/fi"

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  })
  const [showPassword, setShowPassword] = useState(false)
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)

  const { login } = useAuth()
  const navigate = useNavigate()

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    // Basic validation
    if (!formData.email) {
      setError("Email is required")
      return
    }

    if (!formData.password) {
      setError("Password is required")
      return
    }

    setLoading(true)

    try {
      const result = await login(
        formData.email.toLowerCase().trim(),
        formData.password
      )

      if (result.success) {
        navigate("/")
      } else {
        setError(result.error)
      }
    } catch (err) {
      setError("Login failed. Please try again.")
    } finally {
      setLoading(false)
    }
  }

  return (
    <div
      className="auth-container"
      style={{
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background:
          "linear-gradient(120deg, #f8fafc 0%, #e0f7ef 100%)",
      }}
    >
      <div
        className="auth-card"
        style={{
          background: "rgba(255,255,255,0.85)",
          borderRadius: "1.5rem",
          boxShadow: "0 20px 60px #10b98122",
          maxWidth: 420,
          padding: "3rem 2rem",
          width: "100%",
          backdropFilter: "blur(16px) saturate(180%)",
          border: "1.5px solid #e0e7ef",
          transition: "box-shadow 0.2s",
        }}
      >
        <div
          className="auth-header"
          style={{ textAlign: "center", marginBottom: "2rem" }}
        >
          <h1
            style={{
              fontWeight: 800,
              fontSize: "2.25rem",
              color: "#10b981",
              letterSpacing: "0.01em",
              marginBottom: "0.5rem",
            }}
          >
            Welcome Back
          </h1>
          <p style={{ color: "#6b7280", fontSize: "1.1rem" }}>
            Sign in to your account to continue your healthy journey
          </p>
        </div>

        <form
          onSubmit={handleSubmit}
          className="auth-form"
          autoComplete="on"
          style={{ marginBottom: "2rem" }}
        >
          {error && (
            <div
              className="error-message"
              style={{ marginBottom: "1rem" }}
            >
              {error}
            </div>
          )}

          <div
            className="demo-info"
            style={{
              background:
                "linear-gradient(90deg, #e3f2fd 0%, #d1fae5 100%)",
              border: "1px solid #10b98133",
              borderRadius: "0.75rem",
              padding: "12px",
              marginBottom: "20px",
              fontSize: "14px",
              color: "#059669",
              textAlign: "center",
            }}
          >
            <strong>Demo Mode:</strong> Register first to create an account,
            then use those credentials to log in.
          </div>

          <div className="form-group" style={{ marginBottom: "1.5rem" }}>
            <label
              htmlFor="email"
              className="form-label"
              style={{
                fontWeight: 600,
                color: "#374151",
                marginBottom: 6,
                display: "block",
              }}
            >
              <FiMail
                style={{
                  marginRight: 6,
                  color: "#10b981",
                }}
              />{" "}
              Email Address
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter your email"
              required
              autoComplete="username"
              style={{
                border: "2px solid #e5e7eb",
                borderRadius: "0.75rem",
                fontSize: "1rem",
                padding: "0.75rem 1.25rem",
                width: "100%",
                background: "#f8fafc",
                boxShadow: "0 1px 3px #10b98111",
                transition:
                  "border-color 0.2s, box-shadow 0.2s",
                marginBottom: 0,
              }}
            />
          </div>

          <div className="form-group" style={{ marginBottom: "2rem" }}>
            <label
              htmlFor="password"
              className="form-label"
              style={{
                fontWeight: 600,
                color: "#374151",
                marginBottom: 6,
                display: "block",
              }}
            >
              <FiLock
                style={{
                  marginRight: 6,
                  color: "#10b981",
                }}
              />{" "}
              Password
            </label>
            <div
              className="password-input"
              style={{ position: "relative" }}
            >
              <input
                type={showPassword ? "text" : "password"}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="form-input"
                placeholder="Enter your password"
                required
                autoComplete="current-password"
                style={{
                  border: "2px solid #e5e7eb",
                  borderRadius: "0.75rem",
                  fontSize: "1rem",
                  padding: "0.75rem 1.25rem",
                  width: "100%",
                  background: "#f8fafc",
                  boxShadow: "0 1px 3px #10b98111",
                  transition:
                    "border-color 0.2s, box-shadow 0.2s",
                }}
              />
              <button
                type="button"
                className="password-toggle"
                aria-label={
                  showPassword ? "Hide password" : "Show password"
                }
                onClick={() => setShowPassword(!showPassword)}
                style={{
                  position: "absolute",
                  right: 12,
                  top: "50%",
                  transform: "translateY(-50%)",
                  background: "none",
                  border: "none",
                  color: "#10b981",
                  cursor: "pointer",
                  padding: 4,
                  borderRadius: 4,
                  fontSize: 22,
                  transition: "color 0.2s",
                }}
              >
                {showPassword ? <FiEyeOff /> : <FiEye />}
              </button>
            </div>
          </div>

          <button
            type="submit"
            className="btn btn-primary btn-full"
            disabled={loading}
            style={{
              width: "100%",
              marginTop: "1rem",
              background:
                "linear-gradient(90deg, #10b981 0%, #059669 100%)",
              color: "#fff",
              fontWeight: 700,
              fontSize: "1.1rem",
              borderRadius: "0.75rem",
              padding: "0.85rem 0",
              boxShadow: "0 2px 12px #10b98122",
              border: "none",
              transition:
                "background 0.2s, box-shadow 0.2s",
            }}
            aria-busy={loading}
          >
            {loading ? "Signing In..." : "Sign In"}
          </button>
        </form>

        <div
          className="auth-footer"
          style={{
            textAlign: "center",
            paddingTop: "2rem",
            borderTop: "1px solid #e5e7eb",
          }}
        >
          <p style={{ color: "#6b7280" }}>
            Don't have an account?{" "}
            <Link
              to="/register"
              className="auth-link"
              style={{
                color: "#10b981",
                fontWeight: 600,
                textDecoration: "none",
              }}
            >
              Sign up here
            </Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default Login
