import React, { useState, useEffect } from "react"
import { useParams, useNavigate } from "react-router-dom"
import {
  FiClock,
  FiUsers,
  FiHeart,
  FiArrowLeft,
  FiPlus,
  FiCheckCircle,
} from "react-icons/fi"
import { useRecipes } from "../hooks/useRecipes"
import "./RecipeDetails.css"

const RecipeDetails = () => {
  const { id } = useParams()
  const navigate = useNavigate()
  const { getRecipe, addToFavorites, removeFromFavorites } = useRecipes()
  const [recipe, setRecipe] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [isFavorite, setIsFavorite] = useState(false)
  const [servings, setServings] = useState(1)

  // Sample recipe data (in real app, this would come from API)
  const sampleRecipe = {
    id: 1,
    title: "Mediterranean Quinoa Bowl",
    description:
      "A nutritious bowl packed with quinoa, fresh vegetables, and Mediterranean flavors. This recipe is perfect for a healthy lunch or dinner and provides all the essential nutrients your body needs.",
    image: "🥗",
    calories: 320,
    cookingTime: 25,
    prepTime: 15,
    servings: 2,
    category: "lunch",
    tags: ["vegetarian", "high-protein", "mediterranean", "gluten-free"],
    difficulty: "Easy",
    rating: 4.8,
    reviews: 156,
    ingredients: [
      { name: "Quinoa", amount: "1 cup", category: "grains" },
      { name: "Cherry tomatoes", amount: "1 cup", category: "vegetables" },
      { name: "Cucumber", amount: "1 medium", category: "vegetables" },
      { name: "Red onion", amount: "1/4 cup", category: "vegetables" },
      { name: "Kalamata olives", amount: "1/4 cup", category: "pantry" },
      { name: "Feta cheese", amount: "1/4 cup", category: "dairy" },
      { name: "Extra virgin olive oil", amount: "2 tbsp", category: "pantry" },
      { name: "Lemon juice", amount: "2 tbsp", category: "pantry" },
      { name: "Fresh parsley", amount: "1/4 cup", category: "herbs" },
      { name: "Salt and pepper", amount: "to taste", category: "pantry" },
    ],
    instructions: [
      "Rinse quinoa in cold water until water runs clear. In a medium saucepan, bring 2 cups of water to boil.",
      "Add quinoa, reduce heat to low, cover and simmer for 15 minutes or until water is absorbed.",
      "Remove from heat and let stand 5 minutes. Fluff with a fork and let cool completely.",
      "Meanwhile, dice the cucumber and red onion. Halve the cherry tomatoes.",
      "In a large bowl, combine cooled quinoa, tomatoes, cucumber, red onion, and olives.",
      "In a small bowl, whisk together olive oil, lemon juice, salt, and pepper.",
      "Pour dressing over quinoa mixture and toss to combine.",
      "Top with crumbled feta cheese and fresh parsley before serving.",
      "Serve immediately or refrigerate for up to 3 days.",
    ],
    nutrition: {
      calories: 320,
      protein: 12,
      carbs: 45,
      fat: 14,
      fiber: 6,
      sugar: 8,
      sodium: 380,
    },
    tips: [
      "For best flavor, let the salad sit for 30 minutes before serving to allow flavors to meld.",
      "This recipe keeps well in the refrigerator for up to 3 days.",
      "You can substitute the feta cheese with goat cheese or omit for a vegan version.",
      "Add grilled chicken or chickpeas for extra protein.",
    ],
  }

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        setLoading(true)
        setError(null)
        const recipeData = await getRecipe(id)

        if (recipeData) {
          setRecipe(recipeData)
          setServings(recipeData.servings)
        } else {
          setError('Recipe not found')
        }
      } catch (err) {
        console.error("Error fetching recipe:", err)
        setError('Failed to load recipe')
      } finally {
        setLoading(false)
      }
    }

    if (id) {
      fetchRecipe()
    }
  }, [id, getRecipe])

  const handleFavoriteToggle = async () => {
    try {
      if (isFavorite) {
        const success = await removeFromFavorites(recipe.id)
        if (success) {
          setIsFavorite(false)
        }
      } else {
        const success = await addToFavorites(recipe.id)
        if (success) {
          setIsFavorite(true)
        }
      }
    } catch (err) {
      console.error('Error toggling favorite:', err)
    }
  }

  const handleServingsChange = (newServings) => {
    if (newServings > 0) {
      setServings(newServings)
    }
  }

  const getAdjustedAmount = (originalAmount, originalServings) => {
    const multiplier = servings / originalServings
    const match = originalAmount.match(/^(\d+(?:\.\d+)?)\s*(.*)$/)

    if (match) {
      const amount = parseFloat(match[1])
      const unit = match[2]
      const adjustedAmount = (amount * multiplier)
        .toFixed(2)
        .replace(/\.?0+$/, "")
      return `${adjustedAmount} ${unit}`
    }

    return originalAmount
  }

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading recipe...</p>
      </div>
    )
  }

  if (error || !recipe) {
    return (
      <div className="error-page">
        <h2>{error || 'Recipe not found'}</h2>
        <button
          onClick={() => navigate("/recipes")}
          className="btn btn-primary"
        >
          Back to Recipes
        </button>
      </div>
    )
  }

  return (
    <div className="recipe-details">
      <div className="container">
        {/* Header */}
        <div className="recipe-header">
          <button onClick={() => navigate("/recipes")} className="back-btn">
            <FiArrowLeft /> Back to Recipes
          </button>

          <div className="recipe-hero">
            <div className="recipe-image-large">
              <span className="recipe-emoji-large">{recipe.image}</span>
            </div>

            <div className="recipe-info">
              <div className="recipe-badges">
                {recipe.tags.map((tag) => (
                  <span key={tag} className="tag">
                    {tag}
                  </span>
                ))}
              </div>

              <h1 className="recipe-title">{recipe.title}</h1>
              <p className="recipe-description">{recipe.description}</p>

              <div className="recipe-meta">
                <div className="meta-item">
                  <FiClock />
                  <span>{recipe.cookingTime} min</span>
                </div>
                <div className="meta-item">
                  <FiUsers />
                  <span>{recipe.servings} servings</span>
                </div>
                <div className="meta-item calories">
                  <span>{recipe.calories} cal</span>
                </div>
                <div className="meta-item">
                  <span>
                    ⭐ {recipe.rating} ({recipe.reviews} reviews)
                  </span>
                </div>
              </div>

              <div className="recipe-actions">
                <button
                  onClick={handleFavoriteToggle}
                  className={`btn btn-outline ${isFavorite ? "favorited" : ""}`}
                >
                  <FiHeart /> {isFavorite ? "Favorited" : "Add to Favorites"}
                </button>
                <button className="btn btn-primary">
                  <FiPlus /> Add to Meal Plan
                </button>
              </div>
            </div>
          </div>
        </div>

        <div className="recipe-content">
          {/* Nutrition Info */}
          <div className="nutrition-card">
            <h3>Nutrition Facts</h3>
            <div className="nutrition-grid">
              <div className="nutrition-item">
                <span className="nutrition-value">
                  {Math.round(
                    (recipe.nutrition.calories * servings) / recipe.servings
                  )}
                </span>
                <span className="nutrition-label">Calories</span>
              </div>
              <div className="nutrition-item">
                <span className="nutrition-value">
                  {Math.round(
                    (recipe.nutrition.protein * servings) / recipe.servings
                  )}
                  g
                </span>
                <span className="nutrition-label">Protein</span>
              </div>
              <div className="nutrition-item">
                <span className="nutrition-value">
                  {Math.round(
                    (recipe.nutrition.carbs * servings) / recipe.servings
                  )}
                  g
                </span>
                <span className="nutrition-label">Carbs</span>
              </div>
              <div className="nutrition-item">
                <span className="nutrition-value">
                  {Math.round(
                    (recipe.nutrition.fat * servings) / recipe.servings
                  )}
                  g
                </span>
                <span className="nutrition-label">Fat</span>
              </div>
              <div className="nutrition-item">
                <span className="nutrition-value">
                  {Math.round(
                    (recipe.nutrition.fiber * servings) / recipe.servings
                  )}
                  g
                </span>
                <span className="nutrition-label">Fiber</span>
              </div>
              <div className="nutrition-item">
                <span className="nutrition-value">
                  {Math.round(
                    (recipe.nutrition.sodium * servings) / recipe.servings
                  )}
                  mg
                </span>
                <span className="nutrition-label">Sodium</span>
              </div>
            </div>
          </div>

          <div className="recipe-main">
            {/* Ingredients */}
            <div className="ingredients-section">
              <div className="section-header">
                <h3>Ingredients</h3>
                <div className="servings-adjuster">
                  <span>Servings:</span>
                  <button
                    onClick={() => handleServingsChange(servings - 1)}
                    className="serving-btn"
                  >
                    -
                  </button>
                  <span className="serving-count">{servings}</span>
                  <button
                    onClick={() => handleServingsChange(servings + 1)}
                    className="serving-btn"
                  >
                    +
                  </button>
                </div>
              </div>

              <div className="ingredients-list">
                {recipe.ingredients.map((ingredient, index) => (
                  <div key={index} className="ingredient-item">
                    <input type="checkbox" id={`ingredient-${index}`} />
                    <label htmlFor={`ingredient-${index}`}>
                      <span className="ingredient-amount">
                        {getAdjustedAmount(ingredient.amount, recipe.servings)}
                      </span>
                      <span className="ingredient-name">{ingredient.name}</span>
                    </label>
                  </div>
                ))}
              </div>
            </div>

            {/* Instructions */}
            <div className="instructions-section">
              <h3>Instructions</h3>
              <div className="instructions-list">
                {recipe.instructions.map((instruction, index) => (
                  <div key={index} className="instruction-item">
                    <div className="instruction-number">{index + 1}</div>
                    <div className="instruction-text">{instruction}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Tips */}
          <div className="tips-section">
            <h3>Chef's Tips</h3>
            <div className="tips-list">
              {recipe.tips.map((tip, index) => (
                <div key={index} className="tip-item">
                  <FiCheckCircle className="tip-icon" />
                  <p>{tip}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default RecipeDetails
