import React, { createContext, useContext, useState, useEffect } from "react"
import axios from "axios"

const AuthContext = createContext()

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null)
  const [loading, setLoading] = useState(true)
  const [isAuthenticated, setIsAuthenticated] = useState(false)

  // Base URL for your backend
  const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:3004"

  // Set up axios defaults
  useEffect(() => {
    const token = localStorage.getItem("token")
    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`
    }
  }, [])

  // Check if user is logged in on app start
  useEffect(() => {
    checkAuthStatus()
  }, [])

  const checkAuthStatus = async () => {
    try {
      const token = localStorage.getItem("token")
      if (!token) {
        setLoading(false)
        return
      }

      const response = await axios.get(`${API_BASE_URL}/api/auth/me`, {
        headers: { Authorization: `Bearer ${token}` },
      })

      if (response.data.success) {
        setUser(response.data.user)
        setIsAuthenticated(true)
      } else {
        localStorage.removeItem("token")
        delete axios.defaults.headers.common["Authorization"]
      }
    } catch (error) {
      console.error("Auth check failed:", error)
      localStorage.removeItem("token")
      delete axios.defaults.headers.common["Authorization"]
    } finally {
      setLoading(false)
    }
  }

  // Login function
  const login = async (email, password) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/auth/login`, {
        email,
        password,
      })

      if (response.data.success) {
        const { token, user } = response.data

        // Store token in localStorage
        localStorage.setItem("token", token)

        // Set axios default header
        axios.defaults.headers.common["Authorization"] = `Bearer ${token}`

        // Update state
        setUser(user)
        setIsAuthenticated(true)

        return { success: true, user }
      } else {
        throw new Error(response.data.message || "Login failed")
      }
    } catch (error) {
      console.error("Login error:", error)
      throw new Error(error.response?.data?.message || "Login failed")
    }
  }

  // Register function
  const register = async (userData) => {
    try {
      const response = await axios.post(
        `${API_BASE_URL}/api/auth/register`,
        userData
      )

      if (response.data.success) {
        const { token, user } = response.data

        // Store token in localStorage
        localStorage.setItem("token", token)

        // Set axios default header
        axios.defaults.headers.common["Authorization"] = `Bearer ${token}`

        // Update state
        setUser(user)
        setIsAuthenticated(true)

        return { success: true, user }
      } else {
        throw new Error(response.data.message || "Registration failed")
      }
    } catch (error) {
      console.error("Registration error:", error)
      throw new Error(error.response?.data?.message || "Registration failed")
    }
  }

  // Update user function
  const updateUser = async (userData) => {
    try {
      const response = await axios.put(
        `${API_BASE_URL}/api/auth/profile`,
        userData,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      )

      if (response.data.success) {
        const updatedUser = response.data.user

        // Update state
        setUser(updatedUser)

        // Also update localStorage if you store user data there
        const existingProfile = localStorage.getItem(`profile_${user.id}`)
        if (existingProfile) {
          const profile = JSON.parse(existingProfile)
          const updatedProfile = { ...profile, ...userData }
          localStorage.setItem(`profile_${user.id}`, JSON.stringify(updatedProfile))
        }

        return { success: true, user: updatedUser }
      } else {
        throw new Error(response.data.message || "Update failed")
      }
    } catch (error) {
      console.error("Update user error:", error)
      throw new Error(error.response?.data?.message || "Failed to update profile")
    }
  }

  // Change password function
  const changePassword = async (currentPassword, newPassword) => {
    try {
      const response = await axios.put(
        `${API_BASE_URL}/api/auth/change-password`,
        {
          currentPassword,
          newPassword,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      )

      if (response.data.success) {
        return { success: true, message: "Password changed successfully" }
      } else {
        throw new Error(response.data.message || "Password change failed")
      }
    } catch (error) {
      console.error("Change password error:", error)
      throw new Error(error.response?.data?.message || "Failed to change password")
    }
  }

  // Delete account function
  const deleteAccount = async () => {
    try {
      const response = await axios.delete(`${API_BASE_URL}/api/auth/account`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      })

      if (response.data.success) {
        // Clear all user data
        localStorage.removeItem("token")
        localStorage.removeItem(`profile_${user.id}`)
        localStorage.removeItem(`notifications_${user.id}`)
        localStorage.removeItem(`privacy_${user.id}`)
        delete axios.defaults.headers.common["Authorization"]

        // Update state
        setUser(null)
        setIsAuthenticated(false)

        return { success: true, message: "Account deleted successfully" }
      } else {
        throw new Error(response.data.message || "Account deletion failed")
      }
    } catch (error) {
      console.error("Delete account error:", error)
      throw new Error(error.response?.data?.message || "Failed to delete account")
    }
  }

  // Logout function
  const logout = async () => {
    try {
      // Call logout endpoint (optional)
      await axios.post(
        `${API_BASE_URL}/api/auth/logout`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      )
    } catch (error) {
      console.error("Logout endpoint error:", error)
    } finally {
      // Clear local storage and state regardless of API call success
      localStorage.removeItem("token")
      delete axios.defaults.headers.common["Authorization"]
      setUser(null)
      setIsAuthenticated(false)
    }
  }

  const value = {
    user,
    loading,
    isAuthenticated,
    login,
    register,
    updateUser, // Add this
    changePassword, // Add this
    deleteAccount, // Add this
    logout,
    checkAuthStatus,
  }

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  )
}

export function useAuth() {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider")
  }
  return context
}
