    ],
  },
]

class RecipesAPI {
  // Get all recipes with optional filters
  async getRecipes(params = {}) {
    try {
      const response = await api.get("/recipes", { params })
      // Handle backend API response structure which has recipes in response.data.recipes
      const recipes = response.data.recipes || response.data || []
      return {
        success: true,
        data: Array.isArray(recipes) ? recipes : [],
      }
    } catch (error) {
      // Fallback to mock data when API is not available
      console.log("API not available, using mock data")
      return {
        success: true,
        data: MOCK_RECIPES,
      }
    }
  }

  // Get a single recipe by ID
  async getRecipeById(id) {
    try {
      const response = await api.get(`/recipes/${id}`)
      // Extract the recipe from the backend response structure
      const recipe = response.data.recipe || response.data

      // Transform backend data structure to match frontend expectations
      const transformedRecipe = {
        ...recipe,
        id: recipe._id || recipe.id,
        tags: recipe.dietaryTags || recipe.tags || [],
        tips: recipe.tips || [],
        image: recipe.image || "ðŸ½ï¸", // Default emoji if no image
        // Ensure ingredients and instructions are arrays
        ingredients: recipe.ingredients || [],
        instructions: recipe.instructions || [],
        // Add any missing fields with defaults
        rating: recipe.averageRating || 0,
        reviews: recipe.totalRatings || 0,
        cookingTime: (recipe.prepTime || 0) + (recipe.cookTime || 0),
        nutrition: recipe.nutrition || {},
      }

      return {
        success: true,
        data: transformedRecipe,
      }
    } catch (error) {
      // Fallback to mock data when API is not available
      const recipe = MOCK_RECIPES.find((r) => r.id === parseInt(id))
      if (recipe) {
        return {
          success: true,
          data: recipe,
        }
      }
      return {
        success: false,
        error: "Recipe not found",
        data: null,
      }
    }
  }

  // Search recipes
  async searchRecipes(searchTerm, filters = {}) {
    try {
      const params = {
        search: searchTerm,
        ...filters,
      }
      const response = await api.get("/recipes/search", { params })
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock data search when API is not available
      const filtered = MOCK_RECIPES.filter(
        (recipe) =>
          recipe.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          recipe.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
          recipe.tags.some((tag) =>
            tag.toLowerCase().includes(searchTerm.toLowerCase())
          )
      )
      return {
        success: true,
        data: filtered,
      }
    }
  }

  // Get recipes by category
  async getRecipesByCategory(category) {
    try {
      const response = await api.get(`/recipes/category/${category}`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock data filtered by category
      const filtered = MOCK_RECIPES.filter(
        (recipe) => category === "all" || recipe.category === category
      )
      return {
        success: true,
        data: filtered,
      }
    }
  }

  // Get featured/popular recipes
  async getFeaturedRecipes() {
    try {
      const response = await api.get("/recipes/featured")
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock data (return recipes with high ratings)
      const featured = MOCK_RECIPES.filter((recipe) => recipe.rating >= 4.5)
      return {
        success: true,
        data: featured,
      }
    }
  }

  // Add recipe to favorites
  async addToFavorites(recipeId) {
    try {
      const response = await api.post(`/recipes/${recipeId}/favorite`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      console.error("Error adding to favorites:", error)
      return {
        success: false,
        error: "Failed to add to favorites",
      }
    }
  }

  // Remove recipe from favorites
  async removeFromFavorites(recipeId) {
    try {
      const response = await api.post(`/recipes/${recipeId}/favorite`) // Same endpoint, toggles
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      console.error("Error removing from favorites:", error)
      return {
        success: false,
        error: "Failed to remove from favorites",
      }
    }
  }

  // Check if recipe is in favorites
  async isFavorite(recipeId) {
    try {
      const response = await api.get(`/recipes/${recipeId}/favorite`)
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      console.error("Error checking favorite status:", error)
      return {
        success: false,
        error: "Failed to check favorite status",
      }
    }
  }

  // Get user's favorite recipes
  async getFavorites() {
    try {
      const response = await api.get("/recipes/favorites")
      return {
        success: true,
        data: response.data.favorites || [],
      }
    } catch (error) {
      console.error("Error getting favorites:", error)
      return {
        success: false,
        error: "Failed to get favorite recipes",
      }
    }
  }

  // Rate a recipe
  async rateRecipe(recipeId, rating) {
    try {
      const response = await api.post(`/recipes/${recipeId}/rate`, { rating })
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      console.error("Error rating recipe:", error)
      return {
        success: false,
        error: error.response?.data?.message || "Failed to rate recipe",
      }
    }
  }

  // Get recipe categories
  async getCategories() {
    try {
      const response = await api.get("/recipes/categories")
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock categories
      const categories = [
        { value: "all", label: "All Recipes" },
        { value: "healthy", label: "Healthy" },
        { value: "vegetarian", label: "Vegetarian" },
        { value: "main", label: "Main Dishes" },
        { value: "dessert", label: "Desserts" },
      ]
      return {
        success: true,
        data: categories,
      }
    }
  }

  // Get recipe tags
  async getTags() {
    try {
      const response = await api.get("/recipes/tags")
      return {
        success: true,
        data: response.data,
      }
    } catch (error) {
      // Fallback to mock tags extracted from mock recipes
      const allTags = MOCK_RECIPES.flatMap((recipe) => recipe.tags)
      const uniqueTags = [...new Set(allTags)].map((tag) => ({
        value: tag,
        label: tag,
      }))
      return {
        success: true,
        data: uniqueTags,
      }
    }
  }
}

