import React, { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { FiSave, FiPlus, FiMinus, FiArrowLeft } from "react-icons/fi"
import { useRecipes } from "../hooks/useRecipes"

const CreateRecipe = () => {
  const navigate = useNavigate()
  const { createRecipe } = useRecipes()

  // Form state
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    servings: 4,
    prepTime: 15,
    cookTime: 30,
    difficulty: "medium",
    ingredients: [{ name: "", amount: "", unit: "" }],
    instructions: [{ text: "" }],
    tags: [],
    nutrition: {
      calories: 0,
      protein: 0,
      carbs: 0,
      fat: 0,
      fiber: 0
    },
    isPublished: true
  })

  // Status states
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const [categories, setCategories] = useState([])
  const [newTag, setNewTag] = useState("")

  // Load categories
  useEffect(() => {
    const loadCategories = async () => {
      try {
        // This should be replaced with an actual API call to get categories
        setCategories([
          { _id: "1", name: "Breakfast" },
          { _id: "2", name: "Lunch" },
          { _id: "3", name: "Dinner" },
          { _id: "4", name: "Dessert" },
          { _id: "5", name: "Snack" },
        ])
      } catch (err) {
        console.error("Failed to load categories", err)
      }
    }

    loadCategories()
  }, [])

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData(prev => ({
      ...prev,
      [name]: value
    }))
  }

  // Handle nested nutrition changes
  const handleNutritionChange = (e) => {
    const { name, value } = e.target
    setFormData(prev => ({
      ...prev,
      nutrition: {
        ...prev.nutrition,
        [name]: parseFloat(value) || 0
      }
    }))
  }

  // Handle ingredients changes
  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...formData.ingredients]
    updatedIngredients[index] = {
      ...updatedIngredients[index],
      [field]: value
    }

    setFormData(prev => ({
      ...prev,
      ingredients: updatedIngredients
    }))
  }

  // Add new ingredient
  const addIngredient = () => {
    setFormData(prev => ({
      ...prev,
      ingredients: [...prev.ingredients, { name: "", amount: "", unit: "" }]
    }))
  }

  // Remove ingredient
  const removeIngredient = (index) => {
    if (formData.ingredients.length > 1) {
      setFormData(prev => ({
        ...prev,
        ingredients: prev.ingredients.filter((_, i) => i !== index)
      }))
    }
  }

  // Handle instructions changes
  const handleInstructionChange = (index, value) => {
    const updatedInstructions = [...formData.instructions]
    updatedInstructions[index] = { text: value }

    setFormData(prev => ({
      ...prev,
      instructions: updatedInstructions
    }))
  }

  // Add new instruction
  const addInstruction = () => {
    setFormData(prev => ({
      ...prev,
      instructions: [...prev.instructions, { text: "" }]
    }))
  }

  // Remove instruction
  const removeInstruction = (index) => {
    if (formData.instructions.length > 1) {
      setFormData(prev => ({
        ...prev,
        instructions: prev.instructions.filter((_, i) => i !== index)
      }))
    }
  }

  // Handle adding a tag
  const addTag = () => {
    if (newTag.trim() && !formData.tags.includes(newTag.trim())) {
      setFormData(prev => ({
        ...prev,
        tags: [...prev.tags, newTag.trim()]
      }))
      setNewTag("")
    }
  }

  // Handle removing a tag
  const removeTag = (tag) => {
    setFormData(prev => ({
      ...prev,
      tags: prev.tags.filter(t => t !== tag)
    }))
  }

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      // Validate required fields
      if (!formData.title || !formData.description || !formData.category) {
        setError("Please fill in all required fields")
        setLoading(false)
        return
      }

      // Filter out empty ingredients and instructions
      const cleanedData = {
        ...formData,
        ingredients: formData.ingredients.filter(ing => ing.name.trim()),
        instructions: formData.instructions.filter(ins => ins.text.trim())
      }

      if (cleanedData.ingredients.length === 0) {
        setError("Please add at least one ingredient")
        setLoading(false)
        return
      }

      if (cleanedData.instructions.length === 0) {
        setError("Please add at least one instruction")
        setLoading(false)
        return
      }

      const result = await createRecipe(cleanedData)

      if (result.success) {
        navigate(`/recipes/${result.data._id}`)
      } else {
        setError(result.error || "Failed to create recipe")
      }
    } catch (err) {
      setError("An error occurred. Please try again.")
      console.error("Error creating recipe:", err)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="create-recipe-page">
      <div className="container">
        <div className="page-header">
          <button onClick={() => navigate(-1)} className="btn btn-outline">
            <FiArrowLeft /> Back
          </button>
          <h1>Create New Recipe</h1>
        </div>

        {error && (
          <div className="alert alert-error">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="recipe-form">
          <div className="form-section">
            <h3>Basic Information</h3>

            <div className="form-group">
              <label htmlFor="title">Recipe Title *</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Enter recipe title"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description *</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                placeholder="Brief description of your recipe"
                required
              ></textarea>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="category">Category *</label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Category</option>
                  {categories.map(category => (
                    <option key={category._id} value={category._id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="difficulty">Difficulty</label>
                <select
                  id="difficulty"
                  name="difficulty"
                  value={formData.difficulty}
                  onChange={handleChange}
                >
                  <option value="easy">Easy</option>
                  <option value="medium">Medium</option>
                  <option value="hard">Hard</option>
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="servings">Servings</label>
                <input
                  type="number"
                  id="servings"
                  name="servings"
                  value={formData.servings}
                  onChange={handleChange}
                  min="1"
                />
              </div>

              <div className="form-group">
                <label htmlFor="prepTime">Prep Time (mins)</label>
                <input
                  type="number"
                  id="prepTime"
                  name="prepTime"
                  value={formData.prepTime}
                  onChange={handleChange}
                  min="0"
                />
              </div>

              <div className="form-group">
                <label htmlFor="cookTime">Cook Time (mins)</label>
                <input
                  type="number"
                  id="cookTime"
                  name="cookTime"
                  value={formData.cookTime}
                  onChange={handleChange}
                  min="0"
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h3>Ingredients</h3>
            {formData.ingredients.map((ingredient, index) => (
              <div key={index} className="ingredient-row">
                <div className="form-row">
                  <div className="form-group">
                    <input
                      type="text"
                      placeholder="Ingredient name"
                      value={ingredient.name}
                      onChange={(e) => handleIngredientChange(index, "name", e.target.value)}
                    />
                  </div>

                  <div className="form-group">
                    <input
                      type="text"
                      placeholder="Amount"
                      value={ingredient.amount}
                      onChange={(e) => handleIngredientChange(index, "amount", e.target.value)}
                    />
                  </div>

                  <div className="form-group">
                    <input
                      type="text"
                      placeholder="Unit (e.g., g, cups)"
                      value={ingredient.unit}
                      onChange={(e) => handleIngredientChange(index, "unit", e.target.value)}
                    />
                  </div>

                  <button
                    type="button"
                    onClick={() => removeIngredient(index)}
                    className="btn btn-icon"
                    disabled={formData.ingredients.length === 1}
                  >
                    <FiMinus />
                  </button>
                </div>
              </div>
            ))}

            <button
              type="button"
              onClick={addIngredient}
              className="btn btn-outline btn-small"
            >
              <FiPlus /> Add Ingredient
            </button>
          </div>

          <div className="form-section">
            <h3>Instructions</h3>
            {formData.instructions.map((instruction, index) => (
              <div key={index} className="instruction-row">
                <div className="form-row">
                  <div className="instruction-number">{index + 1}</div>
                  <div className="form-group instruction-input">
                    <textarea
                      placeholder="Instruction step"
                      value={instruction.text}
                      onChange={(e) => handleInstructionChange(index, e.target.value)}
                    ></textarea>
                  </div>

                  <button
                    type="button"
                    onClick={() => removeInstruction(index)}
                    className="btn btn-icon"
                    disabled={formData.instructions.length === 1}
                  >
                    <FiMinus />
                  </button>
                </div>
              </div>
            ))}

            <button
              type="button"
              onClick={addInstruction}
              className="btn btn-outline btn-small"
            >
              <FiPlus /> Add Step
            </button>
          </div>

          <div className="form-section">
            <h3>Tags</h3>
            <div className="tags-input-container">
              <div className="form-row">
                <div className="form-group tags-input">
                  <input
                    type="text"
                    placeholder="Add a tag (e.g., Vegan, Gluten-Free)"
                    value={newTag}
                    onChange={(e) => setNewTag(e.target.value)}
                  />
                </div>
                <button
                  type="button"
                  onClick={addTag}
                  className="btn btn-outline btn-small"
                >
                  <FiPlus /> Add
                </button>
              </div>

              <div className="tags-container">
                {formData.tags.map((tag, index) => (
                  <div key={index} className="tag">
                    <span>{tag}</span>
                    <button
                      type="button"
                      onClick={() => removeTag(tag)}
                      className="tag-remove"
                    >
                      <FiMinus />
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="form-section">
            <h3>Nutrition Information</h3>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="calories">Calories</label>
                <input
                  type="number"
                  id="calories"
                  name="calories"
                  value={formData.nutrition.calories}
                  onChange={handleNutritionChange}
                  min="0"
                />
              </div>

              <div className="form-group">
                <label htmlFor="protein">Protein (g)</label>
                <input
                  type="number"
                  id="protein"
                  name="protein"
                  value={formData.nutrition.protein}
                  onChange={handleNutritionChange}
                  min="0"
                />
              </div>

              <div className="form-group">
                <label htmlFor="carbs">Carbs (g)</label>
                <input
                  type="number"
                  id="carbs"
                  name="carbs"
                  value={formData.nutrition.carbs}
                  onChange={handleNutritionChange}
                  min="0"
                />
              </div>

              <div className="form-group">
                <label htmlFor="fat">Fat (g)</label>
                <input
                  type="number"
                  id="fat"
                  name="fat"
                  value={formData.nutrition.fat}
                  onChange={handleNutritionChange}
                  min="0"
                />
              </div>
            </div>
          </div>

          <div className="form-actions">
            <button
              type="button"
              onClick={() => navigate(-1)}
              className="btn btn-outline"
              disabled={loading}
            >
              Cancel
            </button>

            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? "Creating..." : "Create Recipe"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default CreateRecipe
